JavaScript笔记JS基础打印输出&lt;script&gt;        // 文档输出内容        document.write(&quot;Hello World&quot;)        document.write(&#x27;&lt;h1&gt;一级标题&lt;/h1&gt;&#x27;)        // 页面弹出警告对话框        alert(&quot;弹出的内容&quot;)        // 控制台打印输出        console.log(&quot;Hello World&quot;);&lt;/script&gt;

变量&lt;script&gt;        // 声明变量        let age        // 赋值        age = 18        // 打印输出        console.log(age);        document.write(age)        // 直接声明+赋值        let age2 = 19        document.write(age2)    &lt;/script&gt;

键入&lt;script&gt;        // 用户输入        // prompt(&quot;请输入用户名&quot;)        let name = prompt(&quot;请输入用户名&quot;)        // 打印键入值        document.write(name)        &lt;/script&gt;

变量交换&lt;script&gt;        let num1 = 1        let num2 = 2        let temp        temp = num1        num1 = num2        num2 = temp        document.write(num1,num2)    &lt;/script&gt;

数组&lt;script&gt;        // 声明数组        let  list = [1,2,3,4,5]        // 全部打印        // document.write(list)               // 打印3        document.write(list[2])        // 数组元素的个数        document.write(list.length)    &lt;/script&gt;

常量&lt;script&gt;        // 常量的声明        const PI =3.14        document.write(PI)    &lt;/script&gt;

数据类型与检测&lt;script&gt;        // 检测数据类型 typeof        //基本数字类型        //1.数字类型 Number 包括整数 小数 正负数        // let num =10        // document.write(num)        // + - * 除 / 取余 %        // console.log(1+1);        // 例 算圆的面积        // let r = prompt(&quot;请输入圆的半径&quot;)        // let re = 3.14*r*r        // document.write(re)        // 2.字符串类型string        // let str = &quot;Alax&quot;        // console.log(str);        // 模板字符串 拼接字符串和变量 ``+&#123;&#125;        // let age =18        // document.write(`我今年$&#123;age&#125;岁了`)        // 例        // let name = prompt(&quot;请输入您的姓名&quot;)        // let age = prompt(&quot;请输入您的年龄&quot;)        // document.write(`我叫$&#123;name&#125;,今年$&#123;age&#125;岁了`)        //3.布尔 boolean        // let isCool = true        // console.log(isCool);        // 例子        // console.log(3&gt;4);        // 4.未定义 undefined        // 声明一个变量未赋值即使undefined         // let num        // console.log(typeof num);        // console.log(num);        // 5.空 null        // let a = null        // console.log(a);                // null的数据类型是object        // console.log(typeof a);    &lt;/script&gt;

数据类型转换&lt;script&gt;        // 1.隐式转换        //console.log(1+1); //2        // console.log(&quot;Alax&quot;+1); //Alax1        //console.log(2-2); //0        //console.log(2-&#x27;2&#x27;); //0        //console.log(+12);        //console.log(+&#x27;123&#x27;); //+有转换成数字型的作用                // 2.显示转换        // let str = &#x27;123&#x27;        // console.log(Number(str)); //字符转数字        // console.log(Number(&#x27;AlaxEleven&#x27;)); //无法转换        // let num = prompt(&#x27;输入年纪&#x27;) //num为字符串        // console.log(Number(num)); //字符串转数字        // let num2 = Number(prompt(&quot;请输入年纪&quot;)) //字符串转数字        // let num3 = +prompt(&quot;请输入年纪&quot;) //字符串转数字        // parseInt 取整数部分        // console.log(parseInt(&#x27;12px&#x27;)); //12        // console.log(parseInt(&#x27;12.34px&#x27;));  //12        //console.log(parseInt(&#x27;12.94&#x27;)); //12                // parseFloat 保留小数        // console.log(parseFloat(&#x27;12px&#x27;)); //12        // console.log(parseFloat(&#x27;12.34px&#x27;)); //12.34        // console.log(parseFloat(&#x27;12.94&#x27;)); //12.94    &lt;/script&gt;

综合案例&lt;body&gt;    &lt;h2&gt;订单确认&lt;/h2&gt;    &lt;script&gt;        // 用户输入        let price = +prompt(&quot;请输入商品价格&quot;)        let num = +prompt(&quot;请输入商品数量&quot;)        let address = prompt(&quot;请输入收货地址&quot;)        // 2.计算价格        let total = price * num        // 3.页面打印        document.write(`        &lt;table&gt;        &lt;tr&gt;            &lt;th&gt;商品名称&lt;/th&gt;            &lt;th&gt;商品价格&lt;/th&gt;            &lt;th&gt;商品数量&lt;/th&gt;            &lt;th&gt;总价&lt;/th&gt;            &lt;th&gt;收获地址&lt;/th&gt;        &lt;/tr&gt;        &lt;tr&gt;            &lt;td&gt;小米13Ultra&lt;/td&gt;            &lt;td&gt;$&#123;price&#125;元&lt;/td&gt;            &lt;td&gt;$&#123;num&#125;&lt;/td&gt;            &lt;td&gt;$&#123;total&#125;&lt;/td&gt;            &lt;td&gt;$&#123;address&#125;&lt;/td&gt;        &lt;/tr&gt;    &lt;/table&gt;        `)    &lt;/script&gt;

运算符赋值运算符&lt;script&gt;        let num =1        num=num +1        console.log(num)  //2    &lt;/script&gt;

一元运算符&lt;script&gt;        // let num =10        // num = num +1        // num+=1                // 前置自增        // let i = 1        // ++i        // console.log(i);        // 后置自增        // let j = 1        // j++        // console.log(j);    &lt;/script&gt;

自增运算符&lt;script&gt;        // let num =10        // num = num +1        // num+=1                // 前置自增        // let i = 1        // ++i        // console.log(i);        // 后置自增        // let j = 1        // j++        // console.log(j);    &lt;/script&gt;

比较运算符&lt;script&gt;      /*      &gt; 左边是否大于右边      &lt; 左边是否小于右边      &gt;= 左边是否大于或等于右边      &lt;= 左边是否小于或等于右边      == 左右两边值是否相等      === 左右两边是否类型和值都相等      !== 左右两边是否不全等      */          &lt;/script&gt;

