表达式和语句if单分支语句&lt;script&gt;        //if单分支语句        /*        if(条件)&#123;                    &#125;*/        // 除了0所有的数字都为真        // 除了&#x27;&#x27;所有的字符串都为真        if(true)&#123;            console.log(&quot;语句执行&quot;);        &#125;    &lt;/script&gt;

if双分支语句&lt;script&gt;      // if双分支语句      /*if(条件)&#123;          满足则执行      &#125;      else&#123;          不满足则执行      &#125;*/      // 例子      // 1.用户输入      /*      let name = prompt(&quot;请输入用户名:&quot;)      let pwd = prompt(&quot;请输入密码:&quot;)      // 2.判断输出      if(name === &#x27;Alax&#x27; &amp;&amp; pwd === &#x27;123456&#x27;)      &#123;          alert(&quot;登陆成功&quot;)      &#125;      else&#123;          alert(&quot;用户或密码错误&quot;)      &#125; */      // 2. 判断闰年      // 能被4整除但不能被100整除，或者被400整除      // 用户输入      /*      let year = +prompt(&quot;请输入年份&quot;)      // 判断输出      if(year % 4 ===0 &amp;&amp; year % 100 !==0 || year%400 ===0)      &#123;          alert(`&quot;$&#123;year&#125;年是闰年&quot;`)      &#125;else&#123;          alert(`&quot;$&#123;year&#125;年是平年&quot;`)      &#125;*/  &lt;/script&gt;

if多分支语句&lt;script&gt;        if(条件)&#123;            代码1        &#125;else if(条件2)&#123;            代码2        &#125;else if(条件3)&#123;            代码3        &#125;    &lt;/script&gt;

三元运算符&lt;script&gt;       // 条件 ? 满足条件执行 : 不满足执行       3 &lt; 5 ? alert(&quot;真的&quot;) : alert(&quot;假的&quot;)       // 案例 数字补0       // 输入1个数 小于10 前面补0        // 输入       let num = prompt(&quot;请输入一个数字&quot;)       // 输出       num = num &lt;10 ? 0 +num :num       alert(num)   &lt;/script&gt;

